  getMenuByDescription(event: any) {
    const value = event.target.value;

    this.filteredMenu = this.menu.filter(
        (menu) => menu.description.toUpperCase().search(value.toUpperCase()) > -1);
    if(value.length === 0) {
        this.filteredMenu = this.menu;
    }
}

